import React, { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';

const supabase = createClient(
  'https://wgjbslvpvgymdmlucpyh.supabase.co',
  'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6IndnamJzbHZwdmd5bWRtbHVjcHloIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NjcxODc5NzIsImV4cCI6MjA4Mjc2Mzk3Mn0.NKUxsGVOau6NpUaIsU1MgatvpdvQ8uSMHXeeb0UC4JI'
);

const fmt = (n) => `$${Math.round(n || 0).toLocaleString()}`;

export default function MedinaBudget() {
  const [page, setPage] = useState('dashboard');
  const [data, setData] = useState({ income: [], bills: [], cards: [], loans: [] });
  const [loading, setLoading] = useState(true);

  useEffect(() => { loadData(); }, []);

  const loadData = async () => {
    const [inc, bills, cards, loans] = await Promise.all([
      supabase.from('income_sources').select('*'),
      supabase.from('bills').select('*').eq('active', true),
      supabase.from('credit_cards').select('*').eq('active', true),
      supabase.from('loans').select('*').eq('active', true)
    ]);
    setData({ income: inc.data || [], bills: bills.data || [], cards: cards.data || [], loans: loans.data || [] });
    setLoading(false);
  };

  if (loading) return <div style={s.app}><div style={s.loading}>Loading MEDINA BUDGET...</div></div>;

  return (
    <div style={s.app}>
      <nav style={s.nav}>
        <h1 style={s.title}>ðŸ“Š MEDINA BUDGET</h1>
        <div style={s.links}>
          {['Dashboard', 'Monthly', 'Bills', 'Debt', 'Settings'].map(p => (
            <button key={p} onClick={() => setPage(p.toLowerCase())} 
              style={{ ...s.navBtn, ...(page === p.toLowerCase() && s.navBtnActive) }}>
              {p}
            </button>
          ))}
        </div>
      </nav>
      <main style={s.main}>
        {page === 'dashboard' && <Dashboard data={data} reload={loadData} />}
        {page === 'monthly' && <Monthly data={data} reload={loadData} />}
        {page === 'bills' && <Bills data={data} reload={loadData} />}
        {page === 'debt' && <Debt data={data} reload={loadData} />}
        {page === 'settings' && <Settings data={data} reload={loadData} />}
      </main>
    </div>
  );
}

function Dashboard({ data }) {
  const totalIncome = data.income.reduce((s, i) => s + +i.amount, 0) * 2.17;
  const totalDebt = data.cards.reduce((s, c) => s + +c.balance, 0) + data.loans.reduce((s, l) => s + +l.balance, 0);

  // Calculate breakdowns
  const creditCardBills = data.bills.filter(b => b.category === 'Credit Card' || b.category === 'Loan');
  const rentBills = data.bills.filter(b => b.category === 'Rent');
  const monthlyExpenses = data.bills.filter(b => ['Utility', 'Personal'].includes(b.category) && b.name !== 'Fundrise');

  const creditCardTotal = creditCardBills.reduce((s, b) => s + (b.default_amount || 0), 0);
  const rentTotal = rentBills.reduce((s, b) => s + (b.default_amount || 0), 0);
  const expensesTotal = monthlyExpenses.reduce((s, b) => s + (b.default_amount || 0), 0);

  // Find biggest expense
  const categories = [
    { name: 'Credit Cards', total: creditCardTotal },
    { name: 'Rent', total: rentTotal },
    { name: 'Monthly Expenses', total: expensesTotal }
  ].sort((a, b) => b.total - a.total);

  const biggestExpense = categories[0];
  const percentOfIncome = ((biggestExpense.total / totalIncome) * 100).toFixed(1);

  // Get next 5 bills due
  const today = new Date();
  const currentDay = today.getDate();
  const nextBills = data.bills
    .filter(b => b.due_date >= currentDay)
    .sort((a, b) => a.due_date - b.due_date)
    .slice(0, 5);

  return (
    <div>
      <h1 style={s.h1}>Dashboard</h1>

      <div style={s.grid}>
        <StatCard l="ðŸ’° Monthly Income" v={fmt(totalIncome)} g />
        <StatCard l="ðŸ“‹ Active Bills" v={data.bills.length} />
        <StatCard l="ðŸ’³ Total Debt" v={fmt(totalDebt)} />
        <StatCard l="ðŸ”¥ Biggest Expense" v={biggestExpense.name} sub={`${fmt(biggestExpense.total)} (${percentOfIncome}%)`} />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
        <Card t="Monthly Breakdown">
          <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
              <span>ðŸ’³ Credit Cards</span>
              <span style={{ fontWeight: 600, color: '#4ade80' }}>{fmt(creditCardTotal)}</span>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
              <span>ðŸ  Rent</span>
              <span style={{ fontWeight: 600, color: '#4ade80' }}>{fmt(rentTotal)}</span>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
              <span>ðŸ›’ Monthly Expenses</span>
              <span style={{ fontWeight: 600, color: '#4ade80' }}>{fmt(expensesTotal)}</span>
            </div>
            <div style={{ borderTop: '2px solid #2d2d2d', paddingTop: '0.75rem', marginTop: '0.5rem' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 700, fontSize: '1.1rem' }}>
                <span>Total</span>
                <span style={{ color: '#4ade80' }}>{fmt(creditCardTotal + rentTotal + expensesTotal)}</span>
              </div>
            </div>
          </div>
        </Card>

        <Card t="Next Bills Due">
          {nextBills.length === 0 ? (
            <div style={{ color: '#999', textAlign: 'center', padding: '2rem' }}>All bills paid this period!</div>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
              {nextBills.map(bill => (
                <div key={bill.id} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0.75rem', background: '#0a0a0a', borderRadius: '6px' }}>
                  <div>
                    <div style={{ fontWeight: 500 }}>{bill.name}</div>
                    <div style={{ fontSize: '0.85rem', color: '#999' }}>Due: {bill.due_date}{bill.due_date === 1 ? 'st' : bill.due_date === 2 ? 'nd' : bill.due_date === 3 ? 'rd' : 'th'}</div>
                  </div>
                  <div style={{ fontWeight: 600, color: '#4ade80' }}>
                    {bill.varies ? 'â€”' : fmt(bill.default_amount)}
                  </div>
                </div>
              ))}
            </div>
          )}
        </Card>
      </div>
    </div>
  );
}

function Monthly({ data, reload }) {
  const [date, setDate] = useState(new Date());
  const [periods, setPeriods] = useState([]);
  const [paidBills, setPaidBills] = useState({});

  useEffect(() => { loadMonth(); }, [date, data]);

  const togglePaid = (billId) => setPaidBills({ ...paidBills, [billId]: !paidBills[billId] });

  const getPayPeriods = (y, m) => {
    const days = new Date(y, m, 0).getDate();
    const monthName = new Date(y, m - 1).toLocaleDateString('en-US', { month: 'long' });
    return [
      { p: 1, s: new Date(y, m - 1, 1), e: new Date(y, m - 1, 15), l: `${monthName} 1-15` },
      { p: 2, s: new Date(y, m - 1, 16), e: new Date(y, m - 1, days), l: `${monthName} 16-${days}` }
    ];
  };

  const calcPaychecks = (start, end, nextPay, amount) => {
    const checks = [];
    let d = new Date(nextPay);
    while (d < start) d.setDate(d.getDate() + 14);
    while (d <= end) {
      if (d >= start) checks.push({ date: d.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }), amount: +amount });
      d.setDate(d.getDate() + 14);
    }
    return checks;
  };

  const calcIncome = (period, sources) => {
    const jorge = sources.find(s => s.person === 'Jorge') || {};
    const anseli = sources.find(s => s.person === 'Anseli') || {};
    const jChecks = calcPaychecks(period.s, period.e, jorge.next_pay_date, jorge.amount);
    const aChecks = calcPaychecks(period.s, period.e, anseli.next_pay_date, anseli.amount);
    const jTotal = jChecks.reduce((sum, c) => sum + c.amount, 0);
    const aTotal = aChecks.reduce((sum, c) => sum + c.amount, 0);
    return { jTotal, aTotal, total: jTotal + aTotal, jChecks, aChecks };
  };

  const assignBills = (bills, period) => bills.filter(b => period.p === 1 ? b.due_date <= 14 : b.due_date >= 15);

  const calcTransfer = (income, bills) => {
    const jBills = bills.filter(b => b.paid_by === 'Jorge').reduce((s, b) => s + +(b.default_amount || 0), 0);
    const aBills = bills.filter(b => b.paid_by === 'Anseli').reduce((s, b) => s + +(b.default_amount || 0), 0);
    const total = jBills + aBills;
    const leftover = income.total - total;
    const aBalance = income.aTotal - aBills;
    const transfer = aBalance < 0 ? Math.abs(aBalance) : 0;
    return { jBills, aBills, total, leftover, transfer, direction: transfer > 0 ? 'Jorge â†’ Anseli' : '' };
  };

  const loadMonth = () => {
    const y = date.getFullYear();
    const m = date.getMonth() + 1;
    const ps = getPayPeriods(y, m);

    setPeriods(ps.map(p => {
      const income = calcIncome(p, data.income);
      const bills = assignBills(data.bills, p);
      const transfer = calcTransfer(income, bills);
      return { ...p, income, bills, transfer };
    }));
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem', flexWrap: 'wrap', gap: '1rem' }}>
        <h1 style={s.h1}>{date.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</h1>
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <Btn onClick={() => setDate(new Date(date.setMonth(date.getMonth() - 1)))} style={{ padding: '0.5rem 1rem' }}>â† Prev</Btn>
          <Btn onClick={() => setDate(new Date())} style={{ padding: '0.5rem 1rem' }}>Today</Btn>
          <Btn onClick={() => setDate(new Date(date.setMonth(date.getMonth() + 1)))} style={{ padding: '0.5rem 1rem' }}>Next â†’</Btn>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))', gap: '2rem' }}>
        {periods.map(p => (
          <Card key={p.p}>
            <h2 style={s.h2}>{p.l}</h2>

            <div style={{ marginBottom: '1.5rem' }}>
              <h3 style={s.h3}>INCOME</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Jorge:</span><span style={{ color: '#4ade80', fontWeight: 600 }}>{fmt(p.income.jTotal)}</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Anseli:</span><span style={{ color: '#4ade80', fontWeight: 600 }}>{fmt(p.income.aTotal)}</span>
              </div>
              <div style={{ borderTop: '2px solid #2d2d2d', paddingTop: '0.5rem', marginTop: '0.5rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 700 }}>
                  <span>Total:</span><span style={{ color: '#4ade80' }}>{fmt(p.income.total)}</span>
                </div>
              </div>
            </div>

            <div style={{ marginBottom: '1.5rem' }}>
              <h3 style={s.h3}>BILLS DUE ({p.bills.length})</h3>
              {/* Tight rows with checkboxes - ALL bills shown */}
              {p.bills.map(b => (
                <div key={b.id} style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  padding: '0.4rem 0.5rem',
                  marginBottom: '0.25rem',
                  opacity: paidBills[b.id] ? 0.5 : 1,
                  textDecoration: paidBills[b.id] ? 'line-through' : 'none'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', flex: 1 }}>
                    <input 
                      type="checkbox" 
                      checked={paidBills[b.id] || false}
                      onChange={() => togglePaid(b.id)}
                      style={{ width: '16px', height: '16px', cursor: 'pointer' }}
                    />
                    <span style={{ fontSize: '0.9rem' }}>{b.name}</span>
                  </div>
                  <span style={{ color: '#4ade80', fontWeight: 600, fontSize: '0.9rem' }}>
                    {b.default_amount ? fmt(b.default_amount) : 'â€”'}
                  </span>
                </div>
              ))}
              {p.bills.length === 0 && (
                <div style={{ color: '#999', textAlign: 'center', padding: '1rem', fontSize: '0.9rem' }}>No bills due this period</div>
              )}
              <div style={{ borderTop: '2px solid #2d2d2d', paddingTop: '0.5rem', marginTop: '0.5rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 700 }}>
                  <span>Total:</span><span>{fmt(p.transfer.total)}</span>
                </div>
              </div>
            </div>

            <div style={{ borderTop: '2px solid #4ade80', paddingTop: '1rem', marginTop: '1rem' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Total Bills:</span>
                <span style={{ fontWeight: 600 }}>{fmt(p.transfer.total)}</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Total Income:</span>
                <span style={{ fontWeight: 600, color: '#4ade80' }}>{fmt(p.income.total)}</span>
              </div>
              <div style={{ borderTop: '2px solid #2d2d2d', paddingTop: '0.5rem', marginTop: '0.5rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 700 }}>
                  <span>Money Leftover:</span>
                  <span style={{ color: p.transfer.leftover >= 0 ? '#4ade80' : '#ef4444', fontSize: '1.1rem' }}>
                    {fmt(p.transfer.leftover)}
                  </span>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  );
}

function Bills({ data, reload }) {
  const [expanded, setExpanded] = useState({});
  const [editing, setEditing] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [paidBills, setPaidBills] = useState({});

  const toggle = (cat) => setExpanded({ ...expanded, [cat]: !expanded[cat] });
  const togglePaid = (billId) => setPaidBills({ ...paidBills, [billId]: !paidBills[billId] });

  const categories = {
    'Credit Card': { icon: 'ðŸ’³', label: 'Credit Cards', bills: data.bills.filter(b => b.category === 'Credit Card' || b.category === 'Loan') },
    'Rent': { icon: 'ðŸ ', label: 'Rent', bills: data.bills.filter(b => b.category === 'Rent') },
    'Monthly': { icon: 'ðŸ›’', label: 'Monthly Expenses', bills: data.bills.filter(b => ['Utility', 'Personal'].includes(b.category) && b.name !== 'Fundrise') },
    'Investment': { icon: 'ðŸ“ˆ', label: 'Investments', bills: data.bills.filter(b => b.category === 'Investment' || b.name === 'Fundrise') }
  };

  // Split bills into 1st (1-14) and 15th (15-31) periods
  const splitBillsByPeriod = (bills) => {
    const first = bills.filter(b => b.due_date <= 14);
    const fifteenth = bills.filter(b => b.due_date >= 15);
    return { first, fifteenth };
  };

  const startEdit = (bill) => {
    setEditing(bill.id);
    setEditForm({
      id: bill.id,
      name: bill.name || '',
      default_amount: bill.default_amount || '',
      due_date: bill.due_date || 1,
      paid_by: bill.paid_by || 'Anseli',
      payment_method: bill.payment_method || '',
      varies: bill.varies || false
    });
  };

  const saveEdit = async () => {
    try {
      const updates = {
        name: editForm.name,
        default_amount: +editForm.default_amount || null,
        due_date: +editForm.due_date,
        paid_by: editForm.paid_by,
        payment_method: editForm.payment_method
      };

      await supabase.from('bills').update(updates).eq('id', editing);

      setEditing(null);
      setEditForm({});
      reload();
    } catch (error) {
      console.error('Error updating bill:', error);
    }
  };

  const cancelEdit = () => {
    setEditing(null);
    setEditForm({});
  };

  const deleteBill = async (id, name) => {
    if (window.confirm(`Delete "${name}"?`)) {
      await supabase.from('bills').delete().eq('id', id);
      reload();
    }
  };

  const addBill = async (category) => {
    const name = prompt('Bill name:');
    if (!name) return;

    const amount = prompt('Amount (leave empty if varies):');
    const paymentMethod = category === 'Monthly' ? prompt('Payment method (AMEX, Anseli Checking, etc.):') : null;

    await supabase.from('bills').insert([{
      name,
      default_amount: amount ? +amount : null,
      due_date: 1,
      category: category === 'Monthly' ? 'Personal' : category,
      paid_by: 'Anseli',
      payment_method: paymentMethod,
      varies: !amount,
      active: true
    }]);

    reload();
  };

  return (
    <div>
      <h1 style={s.h1}>Bills Management</h1>

      {/* Summary Cards */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))', gap: '1.5rem', marginBottom: '2rem' }}>
        {/* Credit Utilization Card */}
        {(() => {
          const totalBalance = data.cards.reduce((sum, c) => sum + +c.balance, 0);
          const totalLimit = data.cards.reduce((sum, c) => sum + (+c.credit_limit || 0), 0);
          const utilization = totalLimit > 0 ? (totalBalance / totalLimit) * 100 : 0;
          const utilizationColor = utilization < 30 ? '#4ade80' : utilization < 70 ? '#fbbf24' : '#ef4444';

          return (
            <Card t="ðŸ’³ Credit Utilization">
              <div style={{ padding: '1rem' }}>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '1rem', marginBottom: '1.5rem' }}>
                  <div>
                    <div style={{ fontSize: '0.85rem', color: '#999', marginBottom: '0.5rem' }}>Total Balance</div>
                    <div style={{ fontSize: '1.5rem', fontWeight: 700, color: '#ef4444' }}>{fmt(totalBalance)}</div>
                  </div>
                  <div>
                    <div style={{ fontSize: '0.85rem', color: '#999', marginBottom: '0.5rem' }}>Total Limit</div>
                    <div style={{ fontSize: '1.5rem', fontWeight: 700 }}>{totalLimit > 0 ? fmt(totalLimit) : 'â€”'}</div>
                  </div>
                  <div>
                    <div style={{ fontSize: '0.85rem', color: '#999', marginBottom: '0.5rem' }}>Utilization</div>
                    <div style={{ fontSize: '1.5rem', fontWeight: 700, color: utilizationColor }}>
                      {totalLimit > 0 ? `${utilization.toFixed(1)}%` : 'â€”'}
                    </div>
                  </div>
                </div>
                {totalLimit > 0 && (
                  <div>
                    <div style={{ height: '12px', background: '#1a1a1a', borderRadius: '6px', overflow: 'hidden' }}>
                      <div style={{ 
                        height: '100%', 
                        width: `${Math.min(utilization, 100)}%`, 
                        background: utilizationColor,
                        transition: 'width 0.3s ease'
                      }} />
                    </div>
                    <div style={{ fontSize: '0.75rem', color: '#666', marginTop: '0.5rem', textAlign: 'center' }}>
                      {totalLimit > 0 ? `${fmt(totalLimit - totalBalance)} available` : 'Add credit limits on Debt page'}
                    </div>
                  </div>
                )}
                {totalLimit === 0 && (
                  <div style={{ padding: '1rem', background: '#1a1a1a', borderRadius: '6px', textAlign: 'center', color: '#999' }}>
                    Add credit limits on the Debt page to track utilization
                  </div>
                )}
              </div>
            </Card>
          );
        })()}

        {/* Monthly Overview Card */}
        {(() => {
          const creditCardTotal = data.cards.reduce((sum, c) => sum + +c.min_payment, 0) + data.loans.reduce((sum, l) => sum + +l.monthly_payment, 0);
          const rentTotal = data.bills.filter(b => b.category === 'Rent').reduce((sum, b) => sum + (b.default_amount || 0), 0);
          const monthlyTotal = data.bills.filter(b => ['Utility', 'Personal'].includes(b.category) && b.name !== 'Fundrise').reduce((sum, b) => sum + (b.default_amount || 0), 0);
          const investmentTotal = data.bills.filter(b => b.category === 'Investment' || b.name === 'Fundrise').reduce((sum, b) => sum + (b.default_amount || 0), 0);
          const grandTotal = creditCardTotal + rentTotal + monthlyTotal + investmentTotal;

          return (
            <Card t="ðŸ’° Monthly Overview">
              <div style={{ padding: '1rem' }}>
                <div style={{ fontSize: '2rem', fontWeight: 700, color: '#4ade80', marginBottom: '1.5rem', textAlign: 'center' }}>
                  {fmt(grandTotal)}
                  <div style={{ fontSize: '0.85rem', color: '#999', fontWeight: 400, marginTop: '0.25rem' }}>Total Monthly Bills</div>
                </div>
                <div style={{ display: 'grid', gap: '0.75rem' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
                    <span style={{ color: '#999' }}>ðŸ’³ Credit Cards</span>
                    <span style={{ fontWeight: 600 }}>{fmt(creditCardTotal)}</span>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
                    <span style={{ color: '#999' }}>ðŸ  Rent</span>
                    <span style={{ fontWeight: 600 }}>{fmt(rentTotal)}</span>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
                    <span style={{ color: '#999' }}>ðŸ›’ Monthly Expenses</span>
                    <span style={{ fontWeight: 600 }}>{fmt(monthlyTotal)}</span>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0.5rem', background: '#0a0a0a', borderRadius: '6px' }}>
                    <span style={{ color: '#999' }}>ðŸ“ˆ Investments</span>
                    <span style={{ fontWeight: 600 }}>{fmt(investmentTotal)}</span>
                  </div>
                </div>
              </div>
            </Card>
          );
