                style={{ 
                  ...s.input, 
                  padding: '0.5rem',
                  cursor: 'pointer'
                }}
              />
            ) : (
              <div style={{ fontSize: '1.1rem', fontWeight: 600 }}>
                {anseli.next_pay_date ? new Date(anseli.next_pay_date + 'T00:00:00').toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' }) : 'Not set'}
              </div>
